INSERT INTO action_text_rich_texts VALUES(88,'description',replace('<div class="trix-content">\n  Improving BDR/SDR qualification processes\nIncreasing conversions and revenue (MRR) generated by meetings booked from setters/BDRs\nImplementing a qualifying methodology and defining the criterias for defining a high potential lead\nCreating clear criteria for booking meetings with sales reps\nCoaching and listening to calls\nUnderstanding qualitative feedback from prospects\n</div>','\n',char(10)),'Project',88,'2025-01-29 14:12:38.912404','2025-05-20 17:55:10.558667');
