INSERT INTO comments VALUES(195,'<div><strong>Exploration of implementing a chatbot on the website based on success in Brazil &nbsp;</strong></div><ul><li>Brazil implementation has resulted in 7 deals in 2024 and 800K in TCV</li><li>Uses HubSpot chat features with decision-tree qualification flow</li><li>Before implementing in US, need to consult with colin and mg</li><li>US website has approximately 10x more traffic than Brazil site</li><li>The team seems interested in testing this approach</li></ul>',95,2,'2025-04-04 16:27:35.463654','2025-04-04 16:27:35.463654','2025-03-24',1);
